DROP TABLE IF EXISTS `comment`;
CREATE TABLE `comment`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '评论id（主键）',
  `postId` bigint NOT NULL COMMENT '关联的帖子id（关联post表id）',
  `userId` bigint NOT NULL COMMENT '评论者用户id',
  `parentId` bigint NULL DEFAULT 0 COMMENT '父评论id：0=一级评论，>0=二级回复（关联自身id）',
  `toUserId` bigint NULL DEFAULT 0 COMMENT '被回复者用户id（parentId>0时必填）',
  `content` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '评论内容（限制512字）',
