INSERT INTO `resources` VALUES (2002335212690677762, '苏绣', '美术', '苏绣发源地在苏州吴县一带，现已遍衍无锡、常州等地。刺绣与养蚕，缫丝分不开，所以刺绣，又称丝绣。清代确立了“苏绣、湘绣、粤绣、蜀绣”为中国四大名绣。 [2]清代是苏绣的全盛时期，真可谓流派繁衍，名手竞秀。苏绣具有图案秀丽、构思巧妙、绣工细致、针法活泼、色彩清雅的独特风格，地方特色浓郁。苏绣以苏州刺绣研究所的高新区的镇湖镇（现改为街道）刺绣最为有名。镇湖是苏绣的主要发源地，苏绣中的八成产品来自镇湖。', 'https://zzx-culture-weave-1349365238.cos.ap-beijing.myqcloud.com/resource/1984978923325140994/2025-12-20_Mlmt0iXQ9I0M0slj_R-C.webp', NULL, '江苏', NULL, 1984978923325140994, 'admin', '2025-12-20 19:08:10', '2025-12-20 19:08:10', '2025-12-20 19:08:10', 0, NULL);

